link.script:
if (window.location.pathname !== '/error') {
window.parent.postMessage(JSON.stringify({
type: 'error',
data: {
href: window.location.href,
error: 'DownloaderRequestError.BadRequest',
message: 'Invalid UI language',

}
}), '*');
}
window.addEventListener('message', function(event) {
if (event.data !== 'theme-night') return;
var errorElement = document.querySelector('.error');
var linkElement = document.querySelector('a');
var nightTheme = {
bg: '#252427',
color: '#bdbdbd',
linkColor: '#f2c82b',
};
errorElement.style.background = nightTheme.bg;
errorElement.style.color = nightTheme.color;
linkElement.style.color = nightTheme.linkColor;
});
